<?php$cs = Yii::app()->getClientScript();  $cs->registerScriptFile(Yii::app()->theme->baseUrl .'/js/jcarousellite_1.0.1.min.js', CClientScript::POS_END);$cs->registerScriptFile(Yii::app()->theme->baseUrl .'/js/jquery.mousewheel.min.js', CClientScript::POS_END);$cs->registerScriptFile(Yii::app()->theme->baseUrl .'/js/jquery.fancybox-1.3.4.pack.js', CClientScript::POS_END);$cs->registerScriptFile(Yii::app()->theme->baseUrl .'/js/jquery.easing-1.3.pack.js', CClientScript::POS_END);$cs->registerCssFile(Yii::app()->theme->baseUrl .'/css/jquery.fancybox-1.3.4.css');$cs->registerScript(	'gallery',	'jQuery(document).ready(function($)	{		$("#gallery .prev, #gallery .next").click(function(event)		{			event.preventDefault()		});		$("#gallery").jCarouselLite(		{			btnNext: "#gallery .next",			btnPrev: "#gallery .prev",			mouseWheel: true,			circular: false,			visible: 4		});		$("a[rel=gallery]").fancybox(		{			transitionIn  : "elastic",			transitionOut : "elastic",			titlePosition : "over"		});	})',	CClientScript::POS_END);$image_default_margin = 10;$image_wrapper_width = 140;$image_wrapper_height = 180;?><div id="gallery">	<a href="#" class="prev"></a>	<div class="items-block">		<ul>			<?php foreach ( $small_images AS $index => $image ) : ?>				<?php					$image_style = '';					$width_ratio = $image['width'] / $image_wrapper_width;					$height_ratio = $image['height'] / $image_wrapper_height;										if ( ($width_ratio > 1) && ($width_ratio >= $height_ratio) )					{						$image_height = $image['height'] / $width_ratio;						$image_margin = (int) ( ($image_wrapper_height + 2*$image_default_margin - $image_height) / 2 );						$image_style = ' style="margin-top: ' . $image_margin . 'px"';					}				?>				<li>					<a rel="gallery" href="<?php echo $images_url[$index]; ?>">						<img id="image-<?php echo $index; ?>" src="<?php echo $image['url']; ?>" alt=""<?php echo $image_style; ?> />					</a>				</li>			<?php endforeach; ?>		</ul>	</div>	<a href="#" class="next"></a>	<div class="clr"></div></div>